modal.example:
import { Modal, ModalProvider } from './components/ui/modal';
import { Button, Text, Surface } from './components/ui';
import { useState } from 'react';

// 1. Setup ModalProvider in your app root:
<ModalProvider>
  <YourApp />
</ModalProvider>

// 2. Using the modal:
function Example() {
  const [visible, setVisible] = useState(false);

  return (
    <>
      <Button 
        text="Open Modal" 
        onPress={() => setVisible(true)} 
      />

      <Modal 
        visible={visible} 
        onRequestClose={() => setVisible(false)}
      >
        <Surface padding="lg">
          <Text variant="heading" size="lg">Modal Title</Text>
          <Text>Modal content goes here</Text>
          <Button 
            text="Close" 
            onPress={() => setVisible(false)} 
          />
        </Surface>
      </Modal>
    </>
  );
}

// Size variants:
<Modal size="sm">      // Small modal
<Modal size="base">    // Default size
<Modal size="lg">      // Large modal
<Modal size="xl">      // Extra large
<Modal size="full">    // Full screen

// Animation types:
<Modal animationType="fade">   // Fade in/out
<Modal animationType="slide">  // Slide from bottom
<Modal animationType="scale">  // Scale animation
<Modal animationType="none">   // No animation

// Backdrop customization:
<Modal 
  closeOnBackdrop={false}
  backdropOpacity={0.8}
  backdropColor="rgba(0,0,0,0.5)"
/>

