alert-dialog.example:
import { AlertDialog, Button } from './components/ui';
import { useState } from 'react';

function Example() {
  const [open, setOpen] = useState(false);

  return (
    <AlertDialog.Root open={open} onOpenChange={setOpen}>
      <AlertDialog.Trigger asChild>
        <Button variant="destructive" text="Delete" />
      </AlertDialog.Trigger>
      <AlertDialog.Content>
        <AlertDialog.Header>
          <AlertDialog.Title>Are you sure?</AlertDialog.Title>
          <AlertDialog.Description>
            This action cannot be undone.
          </AlertDialog.Description>
        </AlertDialog.Header>
        <AlertDialog.Footer>
          <AlertDialog.Cancel asChild>
            <Button variant="outline" text="Cancel" />
          </AlertDialog.Cancel>
          <AlertDialog.Action asChild>
            <Button variant="destructive" text="Delete" />
          </AlertDialog.Action>
        </AlertDialog.Footer>
      </AlertDialog.Content>
    </AlertDialog.Root>
  );
}

// Variants and sizes:
<AlertDialog.Root variant="destructive">
<AlertDialog.Root variant="warning">
<AlertDialog.Root size="sm">
<AlertDialog.Root size="lg">

// Async actions:
<AlertDialog.Action
  asChild
  closeOnPress={false}
  onPress={async () => await deleteUser()}
>
  <Button variant="destructive" text="Delete" />
</AlertDialog.Action>

// Vertical footer:
<AlertDialog.Footer orientation="vertical">

text-input.example:
import { TextInput } from './components/ui/text-input';
import { useState } from 'react';

function Example() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  return (
    <>
      {/* Basic usage */}
      <TextInput
        label="Email"
        placeholder="Enter your email"
        value={email}
        onChangeText={setEmail}
      />

      {/* Variants */}
      <TextInput variant="outline" placeholder="Outline variant" />
      <TextInput variant="filled" placeholder="Filled variant" />

      {/* Sizes */}
      <TextInput size="sm" placeholder="Small" />
      <TextInput size="base" placeholder="Base" />
      <TextInput size="lg" placeholder="Large" />

      {/* States */}
      <TextInput
        state="error"
        helperText="This field is required"
        placeholder="Error state"
      />
      <TextInput
        state="success"
        helperText="Email is valid"
        placeholder="Success state"
      />

      {/* Secure input */}
      <TextInput
        label="Password"
        secureTextEntry
        value={password}
        onChangeText={setPassword}
      />

      {/* Required field */}
      <TextInput
        label="Required Field"
        required
        placeholder="This field is required"
      />
    </>
  );
}

text-area.example:
import { TextArea } from './components/ui/text-area';
import { useState } from 'react';

function Example() {
  const [description, setDescription] = useState('');
  const [feedback, setFeedback] = useState('');

  return (
    <>
      {/* Basic usage */}
      <TextArea
        label="Description"
        placeholder="Enter description..."
        value={description}
        onChangeText={setDescription}
        rows={4}
      />

      {/* Different row counts */}
      <TextArea rows={2} placeholder="2 rows" />
      <TextArea rows={6} placeholder="6 rows" />
      <TextArea rows={8} placeholder="8 rows" />

      {/* Variants */}
      <TextArea variant="outline" placeholder="Outline variant" />
      <TextArea variant="filled" placeholder="Filled variant" />

      {/* States */}
      <TextArea
        state="error"
        helperText="Message is too short"
        placeholder="Error state"
      />
      <TextArea
        state="success"
        helperText="Perfect length"
        placeholder="Success state"
      />

      {/* Character limit */}
      <TextArea
        label="Feedback"
        value={feedback}
        onChangeText={setFeedback}
        maxLength={500}
        helperText={`${feedback.length}/500 characters`}
        rows={4}
      />

      {/* Required field */}
      <TextArea
        label="Required Message"
        required
        placeholder="This field is required"
        rows={3}
      />
    </>
  );
}

